<script type="text/javascript">
    new Validation('frmreport', {immediate : true});

    Validation.addAllThese([
        ['validate-content', 'Nội dung phải có từ 50 ký tự trở lên', {
            minLength : 50
        }]
    ]);

    $("#fcreport").fancybox({
        helpers:  {
            title:  null,
            overlay: {
                locked: false
            }
        }
    });

    $(document).on('submit','#frmreport',function(e){
        e.preventDefault();
        $('.btn-spinner').find('.after-spinner').hide();
        $('.btn-spinner').find('.spinner').show();
        $.ajax({
            url: '/index.php?page=ajax&do=report-job',
            type: 'post',
            data: $(this).serialize(),
            dataType: 'json',
            success: function (rs) {
                if(rs.status)
                {
                    alert(rs.msg);
                }
                else
                {
                    $('#report-msg').text(rs.msg);
                }
                $('.btn-spinner').find('.after-spinner').show();
                $('.btn-spinner').find('.spinner').hide();
                $('#frmreport').find('input').removeAttr('value');
                $('#frmreport').find('textarea').val('');
                $('#frmreport').find('.error-msg').text('');
            }
        });
    });
</script><div  style="max-width: 1000px; margin: 30px auto">
